import React, { Component } from 'react';
import $ from 'jquery';

import '../AdminCss/studentprofile.css';
import Navbar from '../../SideNavMenu';
import 'jquery-ui-bundle';
import {CircularProgressbar} from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';
import 'jquery-ui-bundle/jquery-ui.css';
// import {Pie,Bar,Line} from 'react-chartjs-2';

const percentage=100;
let progress;

class StudentProfile extends Component {
    
  constructor(props){
    super(props);

    this.state={
      labels: ['Student','Parent','Teacher'],
      datasets:[{
        label:'This Is School Bar Chart',
        data:[1000,700,510],
        backgroundColor:['green','red','blue']
      }],
      student: null
      // {
      //   student_id : '',
      //   student_name:'',
      //   student_address:'',
      //   student_phone:'',
      //   student_roll_no:'',
      //   student_date_of_birth:'',
      //   student_parent_id:'',
      //   student_year:''
      // }
    }
  } 
  componentDidMount() {
    $(function () {
      $(".datepicker").datepicker();
    });

    $("#transport").selectmenu({
      width: 200,
      color: "#fff"
    });
    $("#selectmonth").selectmenu({
      width: 200,
      background: '#fff'
    });
    let student_id = this.props.match.params.id;
    console.log(student_id);
    const student_url = 'http://localhost:8000/students/getOneStudent/'+student_id;
    fetch(student_url)
      .then((response)=>{
        return response.json();
      })
      .then((data)=>{
        this.setState({
          student : data[0]
        },()=>{
          console.log(this.state.student);
        });
      });
  }

  handleOnChange = (event) =>{
    // console.log('Handle Event');
    const studentObj = this.state.student;
    const [name,value] = event.target;
    studentObj[name] = value;
    this.setState({
      student : studentObj
    },()=>{
      console.log(this.state.student);
    })
  }

  render() {
 
    console.log(this.state.student);
    if(percentage>=68 && percentage<=75){
       progress=<CircularProgressbar
      percentage={percentage}
    text={`${percentage}%`}
    styles={{
      path:{
        stroke:'#ff8800'
      },
      text: {
        
        fill: '#ff8800'
      },
    }}
        />
    }
    else if(percentage >=50 && percentage <=67){
      progress=<CircularProgressbar
      percentage={percentage}
    text={`${percentage}%`}
    styles={{
      path:{
        stroke:'red',
        strokeLinecap: 'circular',
      },
       text: {
        
        fill: '#ff0000'
      },
    }}
        />
    }

    else if(percentage>=76 && percentage<=100){
      progress=<CircularProgressbar
      percentage={percentage}
    text={`${percentage}%`}
    styles={{
      path:{
        stroke:'green'
      },
      text: {
        
        fill: '#32cd32'
      },
    }}
        />
    }
    return (
      <div >
        <Navbar/>
        <div className='container'>
          <div className="row">
            <div className="col-xl-8 col-lg-8" >
              <div className='card'>
                <div className='card-body'>
                  <h5 className='card-title'>Profile Infromation</h5>
                  <form>
                    <div className='container'>
                      <div className='row'>
                        <div className='col-xl-6 col-lg-6'>
                          <div className='form-group'>
                            <label>Student Name</label>
                            <input 
                              type='text' 
                              className='form-control' 
                              onChange={this.handleOnChange.bind(this)}
                              //value={this.state.student.student_name}
                              name='student_name'></input>
                          </div>
                        </div>
                        <div className='col-xl-6 col-lg-6'>
                          <div className='form-group'>
                            <label>Password</label>
                            <input type='text' className='form-control' ></input>
                          </div>
                        </div>

                      </div>
                    </div>
                    <div className='container'>
                      <div className='row'>
                        <div className='col-xl-6 col-lg-6'>
                          <div className='form-group'>
                            <label>Roll NO:</label>
                            <input 
                              type='text' 
                              className='form-control'
                              onChange={this.handleOnChange.bind(this)}
                              //value={this.state.student.student_roll_no}
                              name='student_roll_no'></input>
                          </div>
                        </div>
                        <div className='col-xl-6 col-lg-6'>
                          <div className='form-group'>
                            <label>Year</label>
                            <input 
                              type='text' 
                              className='form-control'
                              onChange={this.handleOnChange.bind(this)}
                              //value={this.state.student.student_year} 
                              name='student_year'></input>
                          </div>
                        </div>

                      </div>
                    </div>
                    {/* <div className='container'>
                      <div className='row'>
                        <div className='col-xl-6 col-lg-6'>
                          <div className='form-group'>
                            <label>Class</label>
                            <input type='text' className='form-control'></input>
                          </div>
                        </div>
                        <div className='col-xl-6 col-lg-6'>
                          <div className='form-group'>
                            <label>Section</label>
                            <input type='text' className='form-control'></input>
                          </div>
                        </div>

                      </div>
                    </div> */}
                    <div className='container'>
                      <div className='row'>
                        <div className='col-xl-4 col-lg-4'>
                          <div className='form-group'>
                            <label>Date of Birth</label>
                            <input 
                              type='text' 
                              className='form-control datepicker'
                              onChange={this.handleOnChange.bind(this)}
                              //value={this.state.student.student_date_of_birth} 
                              name='student_date_of_birth'></input>
                          </div>
                        </div>
                        <div className='col-xl-4 col-lg-4'>
                          <div className='form-group'>
                            <label>Phone Number</label>
                            <input 
                              type='text' 
                              className='form-control'
                              onChange={this.handleOnChange.bind(this)}
                              //value={this.state.student.student_phone} 
                              name='student_phone'></input>
                          </div>
                        </div>
                        <div className='col-xl-4 col-lg-4'>
                          <div className='form-group'>
