import React, { Component } from 'react';
import $ from 'jquery';
import { findDOMNode } from 'react-dom';
import '../AdminCss/adminaddstudent.css';
import 'jquery-ui-bundle';
import 'jquery-ui-bundle/jquery-ui.css';
import Navbar from '../../SideNavMenu';



class AdminAddStudent extends Component {

  constructor(){
    super();
    this.state={
      studentData : {
        username : '',
        password : '',
        rollno : '',
        year : '',
        address : '',
        dateofbirth : '',
        phone : '',
        parent : '',
        role : '',
        imageURL : '',
      }
    }
  }

  componentDidMount() {

    $(function () {
      $(".datepicker").datepicker();
    });

    $("#transport").selectmenu({
      width: 200,
      color: "#fff"
    });
    $("#selectmonth").selectmenu({
      width: 200,
      background: '#fff'
    });

    function readURL(input) {
      if (input.files && input.files[0]) {
        var reader = new FileReader();

        reader.onload = function (e) {
          $('#profile-img-tag').attr('src', e.target.result);
        }

        reader.readAsDataURL(input.files[0]);
      }
    }
    $('#profile-img').change(function () {
      readURL(this);
    });
  }

  handleOnChange=(event)=> {
    let obj_tmp = this.state.studentData;
    let {name, value} = event.target;
    obj_tmp[name] = value;
    this.setState({
      studentData : obj_tmp
    },()=>{
      console.log(this.state.studentData);
    })
  }

  handleSubmit=(event)=>{
    event.preventDefault();
    let obj_tmp = this.state.studentData;
    console.log(obj_tmp);
    if(
        obj_tmp.username !== null &&
        obj_tmp.year !== null &&
        obj_tmp.password !== null &&
        obj_tmp.rollno !== null &&
        obj_tmp.address !== null &&
        obj_tmp.dateofbirth !== null &&
        obj_tmp.phone !== null &&
        obj_tmp.parent !== null &&
        obj_tmp.role !== null
      ){
        alert('Save the data to database');
      }else{
        alert('Please Fill All Data');
      }
  }

  render() {
    return (
      <div >
          <Navbar/>
        <div  >
        <div className='container'>
          <div className="row" >
        
            <div className="col-xl-8 col-lg-8"  >
            <div className='container'>
              <div className='card mb-4'>
                <div className='card-body'>
                  <h5 className='card-title'>Profile Infromation</h5>
                  <form onSubmit={this.handleSubmit.bind(this)}>
                    <div className='container'>
                      <div className='row'>
                        <div className='col-xl-6 col-lg-6'>
                          <div className='form-group'>
                            <label>Username</label>
                            <input 
                              type='text'
                              name='username'
                              onChange={this.handleOnChange.bind(this)}
                              className='form-control'
                              required></input>
                          </div>
                        </div>
                        <div className='col-xl-6 col-lg-6'>
                          <div className='form-group'>
                            <label>Password</label>
                            <input 
                              type='text'
                              name='password'
                              onChange={this.handleOnChange.bind(this)}
                              className='form-control'
                              required></input>
                          </div>
                        </div>

                      </div>
                    </div>
                    <div className='container' >
                      <div className='row'>
                        <div className='col-xl-6 col-lg-6'>
                          <div className='form-group'>
                            <label>Roll NO</label>
                            <input 
                              type='text'
                              name='rollno'
                              onChange={this.handleOnChange.bind(this)}
                              className='form-control'
                              required></input>
                          </div>
                        </div>
                        <div className='col-xl-6 col-lg-6'>
                          <div className='form-group'>
                            <label>Year</label>
                            <input 
                              type='text'
                              name='year'
                              onChange={this.handleOnChange.bind(this)}
                              className='form-control'
                              required></input>
                          </div>
                        </div>

                      </div>
                    </div>
                    {/* <div class='container'>
                      <div class='row'>
                        <div class='col-xl-6 col-lg-6'>
                          <div class='form-group'>
                            <label>Class</label>
                            <input type='text' class='form-control'></input>
                          </div>
                        </div>
                        <div class='col-xl-6 col-lg-6'>
                          <div class='form-group'>
                            <label>Section</label>
                            <input type='text' class='form-control'></input>
                          </div>
                        </div>

                      </div>
                    </div> */}
                    <div className='container'>
                      <div className='row'>
                        <div className='col-xl-4 col-lg-4'>
                          <div className='form-group'>
                            <label>Date of Birth</label>
                            <input 
                              type='text'
                              name='dateofbirth'
                              onChange={this.handleOnChange.bind(this)}
                              className='form-control datepicker'
                              required></input>
                          </div>
                        </div>
                        <div className='col-xl-4 col-lg-4'>
                          <div className='form-group'>
                            <label>Phone Number</label>
                            <input 
                              type='text'
                              name='phone' 
                              onChange={this.handleOnChange.bind(this)}
                              className='form-control'
                              required></input>
                          </div>
                        </div>
                        <div className='col-xl-4 col-lg-4'>
                          <div className='form-group'>
                            <label>Address</label>
                            <input 
                              type='text'
                              name='address'
                              onChange={this.handleOnChange.bind(this)} 
                              className='form-control'
                              required></input>
                          </div>
                        </div>

                      </div>
                    </div>
                    <div className='container'>
                      <div className='row'>
                        <div className='col-xl-4 col-lg-4'>
                          <div className='form-group'>
                            <label>Parent</label>
                            <input 
                              type='text'
                              name='parent' 
                              onChange={this.handleOnChange.bind(this)}
                              className='form-control'
                              required></input>
                          </div>
                        </div>
                        <div className='col-xl-4 col-lg-4'>
                          <div className='form-group'>
                            <label>Role</label>
                            <input 
                              type='text'
                              name='role' 
                              onChange={this.handleOnChange.bind(this)}
                              className='form-control'
                              required></input>
                          </div>
                        </div>
                        <div className='col-xl-4 col-lg-4'>

                          <label for="speed">Transport</label>
                          <select name="transport" id="transport">
                            <option>No</option>
                            <option>Yes</option>
                            <option disabled selected>None</option>
                          </select>

                        </div>
                      </div>
                    </div>

                      <div className='container'>
                      <div className='row'>

                      <button className='btn btn-primary col-xl-12'>Save</button>

                      
                    </div>
                    </div>
                  </form>
                </div>
              </div>
              </div>
            </div>
            
            <div className='col-xl-4 col-lg-4'>

            <div className='container'>
              <img src=" " id="profile-img-tag" width='280px' height='280px'></img>

              <input type='file' name='file' placeholder='Choose Photo' id='profile-img'></input>

            </div>
            </div>


        
</div>
        </div>
        </div>
      </div>
    )
  }
}

export default AdminAddStudent;
