import React,{Component} from 'react';
import '../studentrecrod.css';
import {Link} from 'react-router-dom';
import Navbar from '../../SideNavMenu';
import ReactTable from 'react-table';
import 'react-table/react-table.css';

export default class StudentRecord extends Component{
   
    constructor(props){
        super(props);
        
        this.state={
            students:[]
        }
    }
    
    componentDidMount(){
        const url="https://jsonplaceholder.typicode.com/posts";
        fetch(url,{
            method:"GET"
        }).then(res=>res.json()).then(students=>{
            this.setState({students:students})
            console.log(JSON.stringify(students,undefined,2));
        })
    }

    onDelEvent(id) {
        const index=this.state.students.findIndex(students=>{
            return students.id===id;
        })
        this.state.students.splice(index,1);
        this.setState({students:this.state.students});
      }
    render(){
        
        return(
          <div>
              <Navbar />
             
             <div style={{paddingTop:'40px',paddingLeft:'60px',paddingRight:'20px'}}>
             <div class='row '>
                <div class='col-xl-8'>
                    <p class='text' >Student Records</p>
                </div>
                <div class='col-xl-4' > 
                <div style={{textAlign:'right',paddingRight:'10px', paddingTop:'10px' }}>
                  <Link to='adminaddstudent'>  
                  <button class='btn btn-primary' >Add Student</button></Link>
                </div>
                </div>
                </div>
            
           <div class='col-l-12 reacttable' >  
           
               <ReactTable 
               columns={columns}
               data={this.state.students}
               filterable
               defaultPageSize={10}
               noDataText={'Please Wait'}
               >
               </ReactTable>
               </div>
               </div>
             </div>
        )
    }
}
