import React, { Component } from 'react';
import $ from 'jquery';

import './studetnprofile.css';

import 'jquery-ui-bundle';
import CircularProgressbar from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';
import 'jquery-ui-bundle/jquery-ui.css';
// import {Pie,Bar,Line} from 'react-chartjs-2';




const percentage=100;
let progress;



class StudentProfile extends Component {
    
  constructor(props){
    super(props);

    this.state={
      labels: ['Student','Parent','Teacher'],
      datasets:[{
        label:'This Is School Bar Chart',
        data:[1000,700,510],
        backgroundColor:['green','red','blue']
      }]
    }
  } 
  componentDidMount() {
    $(function () {
      $(".datepicker").datepicker();
    });

    $("#transport").selectmenu({
      width: 200,
      color: "#fff"
    });
    $("#selectmonth").selectmenu({
      width: 200,
      background: '#fff'
    });

  }




  render() {
  



    
    if(percentage>=68 && percentage<=75){
       progress=<CircularProgressbar
      percentage={percentage}
    text={`${percentage}%`}
    styles={{
      path:{
        stroke:'#ff8800'
      },
      text: {
        
        fill: '#ff8800'
      },
    }}
        />
    }
    else if(percentage >=50 && percentage <=67){
      progress=<CircularProgressbar
      percentage={percentage}
    text={`${percentage}%`}
    styles={{
      path:{
        stroke:'red',
        strokeLinecap: 'circular',
      },
       text: {
        
        fill: '#ff0000'
      },
    }}
        />
    }

    else if(percentage>=76 && percentage<=100){
      progress=<CircularProgressbar
      percentage={percentage}
    text={`${percentage}%`}
    styles={{
      path:{
        stroke:'green'
      },
      text: {
        
        fill: '#32cd32'
      },
    }}
        />
    }
    return (
      <div >
        <div class='container'>
          <div class="row">
            <div class="col-xl-8 col-lg-8" >
              <div class='card'>
                <div class='card-body'>
                  <h5 class='card-title'>Profile Infromation</h5>
                  <form>
                    <div class='container'>
                      <div class='row'>
                        <div class='col-xl-6 col-lg-6'>
                          <div class='form-group'>
                            <label>Register Number</label>
                            <input type='text' class='form-control'></input>
                          </div>
                        </div>
                        <div class='col-xl-6 col-lg-6'>
                          <div class='form-group'>
                            <label>Register Date</label>
                            <input type='text' class='form-control datepicker' ></input>
                          </div>
                        </div>

                      </div>
                    </div>
                    <div class='container'>
                      <div class='row'>
                        <div class='col-xl-6 col-lg-6'>
                          <div class='form-group'>
                            <label>First Name</label>
                            <input type='text' class='form-control'></input>
                          </div>
                        </div>
                        <div class='col-xl-6 col-lg-6'>
                          <div class='form-group'>
                            <label>Last Name</label>
                            <input type='text' class='form-control'></input>
                          </div>
                        </div>

                      </div>
                    </div>
                    <div class='container'>
                      <div class='row'>
                        <div class='col-xl-6 col-lg-6'>
                          <div class='form-group'>
                            <label>Class</label>
                            <input type='text' class='form-control'></input>
                          </div>
                        </div>
                        <div class='col-xl-6 col-lg-6'>
                          <div class='form-group'>
                            <label>Section</label>
                            <input type='text' class='form-control'></input>
                          </div>
                        </div>

                      </div>
                    </div>
                    <div class='container'>
                      <div class='row'>
                        <div class='col-xl-4 col-lg-4'>
                          <div class='form-group'>
                            <label>Date of Birth</label>
                            <input type='text' class='form-control datepicker'></input>
                          </div>
                        </div>
                        <div class='col-xl-4 col-lg-4'>
                          <div class='form-group'>
                            <label>Phone Number</label>
                            <input type='text' class='form-control'></input>
                          </div>
                        </div>
                        <div class='col-xl-4 col-lg-4'>
                          <div class='form-group'>
                            <label>Address</label>
                            <input type='text' class='form-control'></input>
                          </div>
                        </div>

                      </div>
                    </div>
                    <div class='container'>
                      <div class='row'>
                        <div class='col-xl-4 col-lg-4'>
                          <div class='form-group'>
                            <label>Father Name</label>
                            <input type='text' class='form-control'></input>
                          </div>
                        </div>
                        <div class='col-xl-4 col-lg-4'>
                          <div class='form-group'>
                            <label>Student ID</label>
                            <input type='text' class='form-control'></input>
                          </div>
                        </div>
                        <div class='col-xl-4 col-lg-4'>

                          <label for="speed">Transportion</label>
                          <select name="transport" id="transport">
                            <option>No</option>
                            <option>Yes</option>
                            <option disabled selected>None</option>
                          </select>

                        </div>
                      </div>
                    </div>

                    <div class='container'>
                      <div class='row'>

                        <button class='btn btn-primary col-xl-12'>Save</button>

                      </div>
                    </div>

                  </form>
                </div>
              </div>
            </div>
            <div class='col-xl-4 col-lg-4'>


              <img src='../photo/calendar.png' width='300px' height='200px'></img>


              <div class='card attandance-card'>
                <div class='card-body'>

                  <label  for="#selectmonth">Student Attandance for </label>
                  <select  name="transport" id="selectmonth">
                    <option>Janauary</option>
                    <option>February</option>
                    <option>March</option>
                    <option>April</option>
                    <option>May</option>
                    <option>June</option>
                    <option>July</option>
                    <option>August</option>
                    <option>September</option>
                    <option>October</option>
                    <option>November</option>
                    <option>December</option>

                  </select>
                  {/* just teting piechart show for {attendance percentage} */}
                  <div class='container'>

                  {progress}

                  
                    {/* <Line
                    data={{
