import React,{Component} from 'react';
import './adminteacherrecrod.css';
import {Link} from 'react-router-dom';
import {Col} from 'react-bootstrap';
import ReactTable from 'react-table';
import 'react-table/react-table.css';
import SideNavbar from '../SideNavMenu';





class AdminTeacherRecord extends Component{
   
    constructor(props){
        super(props);
        
        this.state={
            students:[]
        }
    }
    
    componentDidMount(){
        const url = "https://jsonplaceholder.typicode.com/posts";
        fetch(url, {
            method: "GET"
        }).then(res => res.json()).then(teachers => {
            this.setState({ teachers: teachers })
        })
    }
    onDelEvent(id) {
        const index = this.state.teachers.findIndex(teachers => {
            return teachers.id === id;
        })
        this.state.teachers.splice(index, 1);
        this.setState({ teachers: this.state.teachers });
    }
    render() {
        const columns = [
            {
                Header: "Name",
                accessor: "userId",
                Filter: ({ filter, onChange }) => (
                    <input placeholder="Search User"
                        onChange={event => onChange(event.target.value)}
                        value={filter ? filter.value : ''}
                    />
                ),
            },
            {
                Header: "Age",
                accessor: "id",
                filterable: true,
                Filter: ({ filter, onChange }) => (
                    <input placeholder="Search Name"
                        onChange={event => onChange(event.target.value)}
                        value={filter ? filter.value : ''}
                    />
                ),

            },
            {
                Header: "Subject",
                accessor: "title",
                Filter: ({ filter, onChange }) => (
                    <input placeholder="Search Subject"
                        onChange={event => onChange(event.target.value)}
                        value={filter ? filter.value : ''}
                    />
                ),
            },
            {
                Header: "Teacher Profile",
                Cell: props => {
                    return (
                        <button class='btn btn-primary'>See Profile</button>
                    )
                },
                style: {
                    textAlign: 'center'
                },
                width: 150,
                filterable: false
            },
            {
                Header: 'Delete',
                Cell: props => {
                    return (
                        <button class='btn btn-danger ' onClick={() => {
                            this.onDelEvent(props.original.id)
                        }}>Delete</button>
                    )
                },
                style: {
                    textAlign: 'center'
                },
                width: 150,
                filterable: false
            }
        ]
        return(
          <div>
             <SideNavbar/>
             <div style={{paddingTop:'40px',paddingLeft:'60px',paddingRight:'20px'}}>
             <div class='row '>
                <div class='col-xl-8'>
                    <p class='text' >Student Records</p>
                </div>
                <div class='col-xl-4' > 
                <div style={{textAlign:'right',paddingRight:'10px', paddingTop:'10px' }}>
                  <Link to='adminaddstudent'>  
                  <button class='btn btn-primary' >Add Student</button></Link>
                </div>
                </div>
                </div>
            
           <div class='col-l-12 reacttable' >  
           
               <ReactTable 
               columns={columns}
               data={this.state.students}
               filterable
               defaultPageSize={10}
               noDataText={'Please Wait'}
               >
               </ReactTable>
               </div>
               </div>
             </div>
        )
    }
}



export default AdminTeacherRecord;
