import React, { Component } from 'react';
import $ from 'jquery';
import { findDOMNode } from 'react-dom';
import '../AdminCss/adminaddteacher.css';
import 'jquery-ui-bundle';
import 'jquery-ui-bundle/jquery-ui.css';
import Navbar from '../../SideNavMenu';




class AdminAddTeacher extends Component {

  constructor(props){
    super(props);
    this.state={
      subjects : [],
      userRoles : [],
      teacher : {
        username : '',
        phone : '',
        password : '',
        subject : '',
        address : '',
        dateofbirth : '',
        role : '',
        created_by: '',
        updated_by: '',
        imageURL : ''
      }
    }
  }

  componentDidMount() {
    $(function () {
      $(".datepicker").datepicker();
    });

    $("#transport").selectmenu({
      width: 200,
    });


    function readURL(input) {
      if (input.files && input.files[0]) {
        var reader = new FileReader();

        reader.onload = function (e) {
          $('#profile-img-tag').attr('src', e.target.result);
        }

        reader.readAsDataURL(input.files[0]);
      }
    }
    $('#profile-img').change(function () {
      readURL(this);
    });

    //Get Add Subjects & Role
    const subject_url = 'http://localhost:8000/subjects/getAllSubject';
    fetch(subject_url)
      .then((response)=>{
        return response.json();
      })
      .then((data)=>{
        //Set data to state
        this.setState({
          subjects : data
        },()=>{
          console.log(this.state.subjects);
        })
      })

    const user_role_url = 'http://localhost:8000/userRoles/getAllUserRole';
    fetch(user_role_url)
      .then((response)=>{
        return response.json();
      })
      .then((data)=>{
        //Set data to state
        this.setState({
          userRoles : data
        },()=>{
          console.log(this.state.userRoles);
        })
      })
  }




  render() {
    return (
      <div >
        <Navbar />
        <div className='container'>
          <div className="row">
            <div className="col-xl-8 col-lg-8" >
              <div className='card'>
                <div className='card-body'>
                  <h5 className='card-title'>Profile Infromation</h5>
                  <form>
                    {/* <div className='container'>
                      <div className='row'>
                        <div className='col-xl-6 col-lg-6'>
                          <div className='form-group'>
                            <label>Register Number</label>
                            <input type='text' className='form-control'></input>
                          </div>
                        </div>
                        <div className='col-xl-6 col-lg-6'>
                          <div className='form-group'>
                            <label>Register Date</label>
                            <input type='text' className='form-control datepicker'></input>
                          </div>
                        </div>

                      </div>
                    </div> */}
                    <div className='container'>
                      <div className='row'>
                        <div className='col-xl-6 col-lg-6'>
                          <div className='form-group'>
                            <label>Teacher Name</label>
                            <input 
                              type='text' 
                              className='form-control'
                              name='username'
                              ></input>
                          </div>
                        </div>
                        <div className='col-xl-6 col-lg-6'>
                          <div className='form-group'>
                            <label>Password</label>
                            <input 
                              type='text' 
                              className='form-control'
                              name='password'
                              ></input>
                          </div>
                        </div>

                      </div>
                    </div>
                    <div className='container'>
                      <div className='row'>
                        <div className='col-xl-12 col-lg-12'>
                          <div className='form-group'>
                            <label>Subject</label>
                            <select 
                              name='subject'
                              className='form-control'>
                              {
                                this.state.subjects.map(item=>{
                                  return <option value={item.subject_name} key={item.subject_id}>
                                    {item.subject_name}
                                  </option>
                                })
                              }
                            </select>
                          </div>
                        </div>
                        {/* <div className='col-xl-6 col-lg-6'>
                          <div className='form-group'>
                            <label>Section</label>
                            <input type='text' className='form-control'></input>
                          </div>
                        </div> */}

                      </div>
                    </div>
                    <div className='container'>
                      <div className='row'>
                        <div className='col-xl-4 col-lg-4'>
                          <div className='form-group'>
                            <label>Date of Birth</label>
                            <input 
                              type='text' 
                              className='form-control datepicker'
                              name='dateofbirth'
                              ></input>
                          </div>
                        </div>
                        <div className='col-xl-4 col-lg-4'>
                          <div className='form-group'>
                            <label>Phone Number</label>
                            <input 
                              type='text' 
                              className='form-control'
                              name='phone'
                              ></input>
                          </div>
                        </div>
                        <div className='col-xl-4 col-lg-4'>
                          <div className='form-group'>
                            <label>Address</label>
                            <input 
                              type='text' 
                              className='form-control'
                              name='address'
                              ></input>
                          </div>
                        </div>

                      </div>
                    </div>
                    <div className='container'>
                      <div className='row'>
                        <div className='col-xl-6 col-lg-6'>
                          <div className='form-group'>
                            <label>Role</label>
                            <select className='form-control' name='role'>
                              {
                                this.state.userRoles.map(item=>{
                                  return <option value={item.ur_role} key={item.ur_id}>
                                    {item.ur_role}
                                  </option>
                                })
                              }
                            </select>
                          </div>
                        </div>
                        {/* <div className='col-xl-4 col-lg-4'>
                          <div className='form-group'>
                            <label>Student ID</label>
                            <input type='text' className='form-control'></input>
                          </div>
                      </div>*/}
                        <div className='col-xl-4 col-lg-6'>

                          <label for="speed">Transport</label>
                          <select name="transport" id="transport" className='form-control'>
                            <option>No</option>
                            <option>Yes</option>
                            <option disabled selected>None</option>
                          </select>

                        </div>
                      </div>
                    </div>

                    <div className='container'>
                      <div className='row'>

                        <button className='btn btn-primary col-xl-12'>Save</button>

                      </div>
                    </div>

                  </form>
                </div>
              </div>
            </div>
            <div className='col-xl-4 col-lg-4'>


              <img src=" " id="profile-img-tag" width='300px' height='200px'></img>

              <input type='file' name='file' placeholder='Choose Photo' id='profile-img'></input>

            </div>
          </div>


        </div>
      </div>
    )
  }
}

export default AdminAddTeacher;
