import React, { Component } from 'react';
import $ from 'jquery';
import { findDOMNode } from 'react-dom';
import '../AdminCss/adminaddstudent.css';
import 'jquery-ui-bundle';
import 'jquery-ui-bundle/jquery-ui.css';
import Navbar from '../../SideNavMenu';



class AdminAddStudent extends Component {

  constructor(){
    super();
    this.state={
      studentData : {
        username : '',
        password : '',
        rollno : '',
        year : '',
        address : '',
        dateofbirth : '',
        phone : '',
        parent : '',
        role : '',
        imageURL : {},
      },
      userRoleData : []
    }
  }

  componentDidMount() {

    $(function () {
      $(".datepicker").datepicker();
    });

    $("#transport").selectmenu({
      width: 200,
      color: "#fff"
    });
    $("#selectmonth").selectmenu({
      width: 200,
      background: '#fff'
    });

    function readURL(input) {
      if (input.files && input.files[0]) {
        var reader = new FileReader();

        reader.onload = function (e) {
          $('#profile-img-tag').attr('src', e.target.result);
        }

        reader.readAsDataURL(input.files[0]);
      }
    }
    $('#profile-img').change(function () {
      readURL(this);
    });

    //Fetch User Role
    const userRoleURL = 'http://localhost:8000/userRoles/getAllUserRole';
    fetch(userRoleURL)
      .then((response)=>{
        return response.json()
      })
      .then((userRoleData)=>{
        this.setState({
          userRoleData : userRoleData
        },()=>{
          console.log(this.state.userRoleData);
        })
      })
  }

  handleOnChange=(event)=> {
    let obj_tmp = this.state.studentData;
    let {name, value} = event.target;
    obj_tmp[name] = value;
    this.setState({
      studentData : obj_tmp
    },()=>{
      console.log(this.state.studentData);
    })
  }

  handleSubmit=(event)=>{
    event.preventDefault();
    let obj_tmp = this.state.studentData;
    let formData = new FormData();
    formData.append("name","Hla hla");
    formData.append('data',JSON.stringify(obj_tmp));
    formData.append('file',obj_tmp.imageURL);
    console.log(formData);
    const studentDataPostURL = 'http://192.168.1.25:8000/users/addUser';
    fetch('http://192.168.1.25:8000/users/addUser',{
      method : 'POST',
      body : formData
    })
    // .catch(err=>console.log(err))
    //   .then((response)=>{
    //     // console.log(response.json());
    //   })
  }

  fileChangeHandler=(event)=>{
    const file = event.target.files[0];
    let obj_tmp = this.state.studentData;
    obj_tmp.imageURL = file;
    console.log(file);
    this.setState({
      studentData : obj_tmp
    },()=>{
      console.log(this.state.studentData);
    });
  }

  render() {
    return (
      <div >
          <Navbar/>
        <div  >
        <div className='container'>
          <div className="row" >
        
            <div className="col-xl-8 col-lg-8"  >
            <div className='container'>
              <div className='card mb-4'>
                <div className='card-body'>
                  <h5 className='card-title'>Profile Infromation</h5>
                  <form onSubmit={this.handleSubmit.bind(this)}>
                    <div className='container'>
                      <div className='row'>
                        <div className='col-xl-6 col-lg-6'>
                          <div className='form-group'>
                            <label>Username</label>
                            <input 
                              type='text'
                              name='username'
                              onChange={this.handleOnChange.bind(this)}
                              className='form-control'
                              required></input>
                          </div>
                        </div>
                        <div className='col-xl-6 col-lg-6'>
                          <div className='form-group'>
                            <label>Password</label>
                            <input 
                              type='text'
                              name='password'
                              onChange={this.handleOnChange.bind(this)}
                              className='form-control'
                              required></input>
                          </div>
                        </div>

                      </div>
                    </div>
                    <div className='container' >
                      <div className='row'>
                        <div className='col-xl-6 col-lg-6'>
                          <div className='form-group'>
                            <label>Roll NO</label>
                            <input 
                              type='text'
                              name='rollno'
                              onChange={this.handleOnChange.bind(this)}
                              className='form-control'
                              required></input>
                          </div>
                        </div>
                        <div className='col-xl-6 col-lg-6'>
                          <div className='form-group'>
                            <label>Year</label>
                            <input 
                              type='text'
                              name='year'
                              onChange={this.handleOnChange.bind(this)}
                              className='form-control'
                              required></input>
                          </div>
                        </div>

                      </div>
                    </div>
                    {/* <div class='container'>
                      <div class='row'>
                        <div class='col-xl-6 col-lg-6'>
                          <div class='form-group'>
                            <label>Class</label>
                            <input type='text' class='form-control'></input>
                          </div>
                        </div>
                        <div class='col-xl-6 col-lg-6'>
                          <div class='form-group'>
                            <label>Section</label>
                            <input type='text' class='form-control'></input>
                          </div>
                        </div>

                      </div>
                    </div> */}
                    <div className='container'>
                      <div className='row'>
                        <div className='col-xl-4 col-lg-4'>
                          <div className='form-group'>
                            <label>Date of Birth</label>
                            <input 
                              type='text'
                              name='dateofbirth'
                              onChange={this.handleOnChange.bind(this)}
                              className='form-control datepicker'
                              required></input>
                          </div>
                        </div>
                        <div className='col-xl-4 col-lg-4'>
                          <div className='form-group'>
                            <label>Phone Number</label>
                            <input 
                              type='text'
                              name='phone' 
                              onChange={this.handleOnChange.bind(this)}
                              className='form-control'
                              required></input>
                          </div>
                        </div>
                        <div className='col-xl-4 col-lg-4'>
                          <div className='form-group'>
                            <label>Address</label>
                            <input 
                              type='text'
                              name='address'
                              onChange={this.handleOnChange.bind(this)} 
                              className='form-control'
                              required></input>
                          </div>
                        </div>

                      </div>
                    </div>
                    <div className='container'>
                      <div className='row'>
                        <div className='col-xl-4 col-lg-4'>
                          <div className='form-group'>
                            <label>Parent</label>
                            <input 
                              type='text'
                              name='parent' 
                              onChange={this.handleOnChange.bind(this)}
                              className='form-control'
                              required></input>
                          </div>
                        </div>
                        <div className='col-xl-4 col-lg-4'>
                          <div className='form-group'>
                            <label>Role</label>
                            <select 
                              type='text'
                              name='role' 
                              onChange={this.handleOnChange.bind(this)}
                              className='form-control'
                              required>
                                {
                                  this.state.userRoleData.map((item,index)=>{
                                    return <option key={index}>
                                      {
                                        item.ur_role
                                      }
                                    </option>
                                  })
                                }
                              </select>
                          </div>
                        </div>
                        <div className='col-xl-4 col-lg-4'>

                          <label for="speed">Transport</label>
                          <select name="transport" id="transport">
                            <option>No</option>
                            <option>Yes</option>
                            <option disabled selected>None</option>
                          </select>

                        </div>
                      </div>
                    </div>

                      <div className='container'>
                      <div className='row'>

                      <button className='btn btn-primary col-xl-12'>Save</button>

                      
                    </div>
                    </div>
                  </form>
                </div>
              </div>
              </div>
            </div>
            
            <div className='col-xl-4 col-lg-4'>

            <div className='container'>
